$(document).ready(function(){const e=document.querySelector(".marathon-nav__wrapper"),a=document.querySelectorAll(".marathon-nav__item"),t=document.querySelector(".hamburger");t.addEventListener("click",()=>{t.classList.toggle("hamburger_active"),e.classList.toggle("marathon-nav__wrapper_active")}),a.forEach(a=>{a.addEventListener("click",()=>{t.classList.toggle("hamburger_active"),e.classList.toggle("marathon-nav__wrapper_active")})}),$(".slick").slick({adaptiveHeight:!0,autoplay:!0})});