$(document).ready(function(){const e=document.querySelector(".marathon-nav__wrapper"),t=document.querySelectorAll(".marathon-nav__item"),a=document.querySelector(".hamburger");a.addEventListener("click",()=>{a.classList.toggle("hamburger_active"),e.classList.toggle("marathon-nav__wrapper_active")}),t.forEach(t=>{t.addEventListener("click",()=>{a.classList.toggle("hamburger_active"),e.classList.toggle("marathon-nav__wrapper_active")})}),$(".slick").slick({adaptiveHeight:!0,autoplay:!0});const r=document.querySelectorAll('a[href*="#"]');for(let e of r)e.addEventListener("click",function(t){t.preventDefault();const a=e.getAttribute("href").substr(1);document.getElementById(a).scrollIntoView({behavior:"smooth",block:"start"})})});