document.addEventListener("DOMContentLoaded",function(e){window.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".marathon-nav__wrapper"),t=document.querySelectorAll(".marathon-nav__item"),a=document.querySelector(".hamburger");a.addEventListener("click",()=>{a.classList.toggle("hamburger_active"),e.classList.toggle("marathon-nav__wrapper_active")}),t.forEach(t=>{t.addEventListener("click",()=>{a.classList.toggle("hamburger_active"),e.classList.toggle("marathon-nav__wrapper_active")})})})});