document.addEventListener("DOMContentLoaded",function(e){new Typed("#typed",{strings:["Почни вдосконалювати<br>свій голос просто зараз!","Давай зробимо<br>з нього цукерочку!"],typeSpeed:80,backSpeed:30,smartBackspace:!0,loop:!0});$(document).ready(function(){$(".slick").slick({adaptiveHeight:!0,autoplay:!0})});const t=document.querySelectorAll('a[href*="#"]');for(let e of t)e.addEventListener("click",function(t){t.preventDefault();const n=e.getAttribute("href").substr(1);document.getElementById(n).scrollIntoView({behavior:"smooth",block:"start"})})});